// –ú–æ–∫–∏ –¥–∞–Ω–Ω—ã—Ö –¥–ª—è —Ä–∞–∑—Ä–∞–±–æ—Ç–∫–∏
const mockBotResponse = (requestData) => {
  // –ò–º–∏—Ç–∏—Ä—É–µ–º –∑–∞–¥–µ—Ä–∂–∫—É —Å–µ—Ä–≤–µ—Ä–∞
  const delay = Math.floor(Math.random() * 1000) + 500; // –æ—Ç 500 –¥–æ 1500 –º—Å
  
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({
        id: Date.now().toString(),
        type: requestData.type,
        query: requestData.query,
        response: getMockResponseByType(requestData.type, requestData.query),
        model: requestData.model || 'default',
        createdAt: new Date().toISOString(),
      });
    }, delay);
  });
};

